import React, { useState } from "react";
import "./App.css";

const BASE_URL = "http://localhost:8080"; // Replace with IBM Code Engine URL after deployment

function App() {
  const [step, setStep] = useState(1);
  const [inputText, setInputText] = useState("");
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answer, setAnswer] = useState("");
  const [feedback, setFeedback] = useState("");
  const [results, setResults] = useState([]);

  const handleStart = async () => {
    const response = await fetch(`${BASE_URL}/api/generate`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ input: inputText }),
    });
    const data = await response.json();
    setQuestions(data.questions);
    setStep(2);
  };

  const handleSubmitAnswer = async () => {
    const question = questions[currentQuestionIndex];
    const response = await fetch(`${BASE_URL}/api/evaluate`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ question, answer }),
    });
    const data = await response.json();
    setFeedback(data.feedback);
    const newResults = [...results, { question, answer, feedback: data.feedback }];
    setResults(newResults);
    setAnswer("");
    if (currentQuestionIndex + 1 < questions.length) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setStep(3);
    }
  };

  if (step === 1) {
    return (
      <div className="App">
        <h1>Interview Trainer</h1>
        <textarea
          rows="6"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="Paste your resume or enter job role..."
        ></textarea>
        <button onClick={handleStart}>Start Practice</button>
      </div>
    );
  }

  if (step === 2) {
    const q = questions[currentQuestionIndex];
    return (
      <div className="App">
        <h2>Question {currentQuestionIndex + 1}</h2>
        <p>{q}</p>
        <textarea
          rows="4"
          value={answer}
          onChange={(e) => setAnswer(e.target.value)}
          placeholder="Type your answer..."
        ></textarea>
        <button onClick={handleSubmitAnswer}>Submit Answer</button>
        {feedback && <p><strong>Feedback:</strong> {feedback}</p>}
      </div>
    );
  }

  if (step === 3) {
    return (
      <div className="App">
        <h1>Session Complete</h1>
        {results.map((r, i) => (
          <div key={i}>
            <p><strong>Q{i + 1}:</strong> {r.question}</p>
            <p><strong>Your Answer:</strong> {r.answer}</p>
            <p><strong>Feedback:</strong> {r.feedback}</p>
            <hr />
          </div>
        ))}
      </div>
    );
  }

  return null;
}

export default App;
